
public class Basicofjava {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		//Assigning value to variable
		int num =5;    //variable with return type integer
		String s1 = "My name is khan"; //variable with return type String
		char c = 'r'; //variable with return type character
		boolean bool = true; //variable with return type boolean
		double d = 5.99; //variable with return type double

		// How to print variables

		System.out.println(num); //printing a number
		System.out.println(s1); //printing a string
		System.out.println(c); //printing a character
		System.out.println(bool); //printing a boolean
		System.out.println(d);  //printing a double
		
		//Concatenation a string in JAVA
		
		System.out.println(s1+" this a concatenation");
		
		


	}

}
